const Product = ({post}) => {
  return(
    <div className="max-w-[300px] p-4 mb-4 border">
      <div className="p-2">
        <p className="mb-4 font-bold text-lg">{post.title.substring(0,50)}</p>
        <p>{post.description.substring(0,100)}</p>
        <div className="w-full">
          <img src={post.image} alt="productPic" className="w-[200px] h-[250px] mx-auto"></img>
        </div>
      </div>
      <div className="flex justify-between">
        <p>{post.price}</p>
        <button >ADD TO CART</button>
      </div>
    </div>
  )
};

export default Product;
