import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import CartItem from "../components/CartItem"
import { NavLink } from "react-router-dom";

const Cart = () => {
  const{cart} = useSelector((state)=>state)
  const[totalItem,setTotalItem]=useState(0)
  const[totalAmount,setTotalAmount]=useState(0)
  useEffect(()=>{
    setTotalItem(cart.length)
    setTotalAmount(cart.reduce((acc,cur)=>acc+cur.price,0))
  },[cart])
  return (
    <div className="flex max-w-[900px] mx-auto gap-10 mt-12">
      {
        cart.length>0?(<>
              <div className="w-full">
            {
              cart.map((item)=>(
                <CartItem key={item.id} item={item}/>
              ))
            }
          </div>
          <div className="flex flex-col justify-between h-[75vh] min-w-[300px]">
            <div>
              <p>YOUR CART</p>
              <p>SUMMARY</p>
              <p>Total Items : {totalItem}</p>
            </div>
            <div>
              <p className="font-semibold">Total Amount : ${totalAmount}</p>
              <button className="w-full bg-green-500 rounded-xl p-3 text-white hover:bg-green-600 mt-2 font-semibold">Checkout Now</button>
            </div>
          </div>
        </>):(<div className="flex flex-col w-full h-[80vh] items-center justify-center">
                <p>Your Cart is Empty</p>
                <NavLink to='/'>
                  <button>Go to Home</button>
                </NavLink>
        </div>)
      }
    </div>
  )
};

export default Cart;
