import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";

const Product = ({item}) => {
  // const{cart}=useSelector((state)=>state)
  const[itemPresent,setItemPresent]=useState(false)
  const dispatch=useDispatch()
  function removeFromCart(){
    dispatch(remove(item.id))
    setItemPresent(false)
  }
  function addToCart(){
    dispatch(add(item))
    setItemPresent(true)
  }
  // useEffect(()=>{
  //   setItemPresent(()=>{
  //     cart.forEach(element => {
  //       if(element.id===item.id){
  //         return true;
  //       }
  //     });
  //   })
  // },[cart])

  return(
    <div className="max-w-[270px] p-4 mb-4 border">
      <div className="p-2">
        <p className="mb-4 font-bold text-lg">{item.title.substring(0,30)}</p>
        <p className="text-sm">{item.description.substring(0,120)}</p>
        <div className="w-full">
          <img src={item.image} alt="productPic" className="max-w-[160px] max-h-[190px] mx-auto p-1"></img>
        </div>
      </div>
      <div className="flex justify-between">
        <p>$ {item.price}</p>
        {
          itemPresent?<button onClick={removeFromCart}>REMOVE FROM CART</button>:<button onClick={addToCart}>ADD TO CART</button>
        }
        
      </div>
    </div>
  )
};

export default Product;
